INSERT INTO songs_english VALUES("1","1","Dear Uncle Dear Uncle Dear Uncle Dear Uncle","Malaysia Vasudevan, Malaysia Vasudevan, Malaysia Vasudevan","","","<html>\n<head>\n	<title></title>\n</head>\n<body>\n<p><span style=\"color:#000000;\"><span style=\"font-size:14px;\">Cho: Hey lalalalala..<br />\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;dear uncle be happy<br />\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;dear mother is doing well<br />\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;for daddy for mummy one for you uncle<br />\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;on for you uncle<br />\nM: &nbsp; &nbsp; pooppondra ullangal theyvangale<br />\nCho: &nbsp;comeon carry on uncle<br />\nM: &nbsp; &nbsp; pooppondra ullangal theyvangale<br />\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; puyaledhu itiyedhu pillai nenjile<br />\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; arivukal valarndhaale sodhanaikal<br />\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; sila vedhanaikal oomai kaayangal<br />\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; idhu mullai siru killai naan pillai illaiye<br />\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; nilai solla adhai vella naan kallum illaiye kallum illaiye<br />\nCho: &nbsp; &nbsp; &nbsp; &nbsp; (dear uncle)<br />\nM: &nbsp; &nbsp; &nbsp;powrnami vaanaththil nilavillaiye<br />\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;pakal neram vazhi kaatta kadhirillaiye<br />\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;irantukkum iraivaa nee naal vaippadho<br />\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;indha meenkalukke naan enna solluven<br />\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;oru metai adhai mooti vilaiyaata solkindraai<br />\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;naan oomai mozhiyillai ennai paata solkindraai paata solkindraai<br />\nCho: &nbsp; &nbsp; &nbsp; &nbsp; (dear uncle)</span></span></p>\n\n<p>&nbsp;</p>\n</body>\n</html>\n","3:30","Panju Arunachalam","Genre1","FMTimings1","4","","","0","31169","","1");
