INSERT INTO songs_english VALUES("518","1","Kanne Enn Kaarmughile  ","Illayaraja","K.S. Chitra","","<html>\n<head>\n	<title></title>\n</head>\n<body>\n<p>Kanne en kaar mukile kanneerum enatiyo<br />\nponne en poongkotiye poraattam enatiyo<br />\npala naal irul thaan oru naal vitiyum<br />\naanandham kai kootattum &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(kanne)</p>\n\n<p>naanendrum neeyendrum verendru aakaadhu<br />\nkalangum maname kanneerum kootaadhu<br />\npoovotu seraamal kaatrengum pokaadhu<br />\npozhudhum vitiyum poraattam oyaadhu<br />\ntheepaththai pola endrum un vaazhvudhaan<br />\nthiyaakangal unnotudhaan<br />\npaasaththil maaraadha un paarvaidhaan<br />\npaarkkindren unnotudhaan<br />\nthaniyaai irundhen thunaiyaai inaindhaai<br />\nmaaraadhu nam kaadhale &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (kanne)</p>\n\n<p>kalyaana poomaalai potaadha or maadhu<br />\nkanni maalai pottaale ippodhu<br />\ntholotu poomaalai vaaraamal pokaadhu<br />\nthotarum uravu eppodhum maaraadhu<br />\nsokaththil sangeedham sukamaanadhu<br />\nsondhangal thirai potudhu<br />\npaasaththil thunpangal untaanadhu<br />\nvedhangal vilai pesudhu uyirum uravum unarvum neeye<br />\nunnil ival paadhiye &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (kanne)</p>\n</body>\n</html>\n","4:39","","","","1","","","0","31422","","1");
INSERT INTO songs_english VALUES("518","2","Karpudaya Aampalainga","S.P.Balasubramaniyam","","","<html>\n<head>\n	<title></title>\n</head>\n<body>\n<p>Karputaiya aampilainga oorukkulle<br />\nnimmadhiyaai vaazhndhirukka kaalamille<br />\nsandhiranai kantu vandha kaalaththilum unga<br />\nsandheka puththi innum maaravillai<br />\nati kannammaa ennammaa padhil sollammaa<br />\nveravelaiyilla vetti ille &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(karputaiya)</p>\n\n<p>patusuththam maaman manasu unakku piththam erum vayasu<br />\nennaich chuththum saadhik kotiye<br />\nati niththam enti kiliye<br />\npaaththu paaththu naan kaaththa maanam paadhi vazhiyil<br />\npokaadhammaa maanam paadhi vazhiyil pokaadhammaa<br />\nrampa oorvasi vandhaalum koota namma manasu maaraadhammaa<br />\nandhap pakkam indhap pakkam kannu mattum paakkum<br />\nthappu edhum seyyaadhammaa&iexcl;<br />\nennaich chuththi niththam niththam attai pola<br />\nneeyum ottik kolla ventaammaa<br />\natavera vazhi maaraadhammaa maaraadhammaa<br />\naiyo venaam indha velai ippa ennaththukku<br />\nkattalappottu saaththa kaattu vellam meerippokum<br />\nkannupattum kallum koota pasum ponnaa maarakkootum<br />\ney paakkum kannil sandhekam irundhaal<br />\nkayiru koota paampaakum<br />\npaarvai therinju sandhekam marainjaa<br />\npaavai manasum poovaayitum<br />\nsaththiyammaa sollipputten<br />\nsangadhiyai kelu kuththam kurai inge illai<br />\nuththamanum ennaippola oorukkulla yaaru<br />\nunmaiyai nee kelu pulla<br />\nata maaman sri raaman thaan<br />\nati maane veen velai ippa ennaththukku &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (karputaiya)<br />\n&nbsp;</p>\n\n<p>&nbsp;</p>\n</body>\n</html>\n","4:25","","","","3","","","0","31423","","1");
INSERT INTO songs_english VALUES("518","3","Sokku Podi","Arunmozhi","S. Janaki","","<html>\n<head>\n	<title></title>\n</head>\n<body>\n<p>F:&nbsp;&nbsp; &nbsp;Sokkuppoti vachchirukken maamaa<br />\n&nbsp;&nbsp; &nbsp;enna sokkavachchi etti nikkalaamaa<br />\n&nbsp;&nbsp; &nbsp;vekkaththukku vela illai maamaa<br />\n&nbsp;&nbsp; &nbsp;naan ventiyadhai kettupputten aamaa<br />\nM:&nbsp;&nbsp; &nbsp;etti konjam thalli nillu maane<br />\n&nbsp;&nbsp; &nbsp;ille ekkachchakkamaayip pokum thaane<br />\n&nbsp;&nbsp; &nbsp;ulladhellaam sollipputten naane<br />\n&nbsp;&nbsp; &nbsp;ata ootal enna kootal enna maane<br />\nF:&nbsp;&nbsp; &nbsp;needhaane nenjaththile nesaththaiyum<br />\n&nbsp;&nbsp; &nbsp;paasaththaiyum eththi vachcha<br />\n&nbsp;&nbsp; &nbsp;pooppola pen manasai pollaadha<br />\n&nbsp;&nbsp; &nbsp;aasaiyila maaththi vachcha<br />\nM:&nbsp;&nbsp; &nbsp;pen pillai naanam vittaa<br />\n&nbsp;&nbsp; &nbsp;kanniththamizh naattukkadhu aakaadhammaa<br />\n&nbsp;&nbsp; &nbsp;kanmooti aasai vachchaa kaala manam<br />\n&nbsp;&nbsp; &nbsp;kaavalaiththaan thaantaadhammaa<br />\nF:&nbsp;&nbsp; &nbsp;thaanaa kaninja kanidhaan-ippa neeyum naanum thani thaan<br />\nM:&nbsp;&nbsp; &nbsp;emmaa vettikkadhai thaan-ata<br />\n&nbsp;&nbsp; &nbsp;edhukku indha vazhidhaan<br />\nF:&nbsp;&nbsp; &nbsp;ishtappattu nerunga<br />\n&nbsp;&nbsp; &nbsp;indha manam keranga ettu vachchu vandhadhaiyyaa &nbsp; &nbsp;&nbsp;<br />\n&nbsp;&nbsp; &nbsp;un matiyil uranga &nbsp; &nbsp; &nbsp; &nbsp;(sokkkuppoti)</p>\n\n<p>&nbsp;&nbsp; &nbsp;thaniyaaka naan patuththaa thondharavu<br />\n&nbsp;&nbsp; &nbsp;pannudhaiyaa un ninaippu<br />\n&nbsp;&nbsp; &nbsp;ennai neenga aadharichchaa vittu<br />\n&nbsp;&nbsp; &nbsp;vittu otumaiyaa en thavippu<br />\nM:&nbsp;&nbsp; &nbsp;ariyaadha sinnap ponnu achcham konjam<br />\n&nbsp;&nbsp; &nbsp;naanam konjam venumammaa puriyaadha mokaththile<br />\n&nbsp;&nbsp; &nbsp;puththi thatumaari vitak kootumammaa<br />\nF:&nbsp;&nbsp; &nbsp;