INSERT INTO songs_english VALUES("518","4","Then Mathura","","K.S. Chitra","","<html>\n<head>\n	<title></title>\n</head>\n<body>\n<p>Then madhurai seemaiyile meenaatchi kovilile<br />\nkalyaanam eppo sollammaa<br />\nen kannammaa sollaame enti vekkamaa<br />\nthen madhurai seemaiyile meenaatchi kovilile<br />\nkalyaanam pannik kolvom en ponnammaa<br />\nsollaame allik kolvom<br />\nnaan enna solla vaarththai edhum sikkavillai<br />\nnalla thaiyi vellik kaalai<br />\nnaanga maaththa porom maalai &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(then)</p>\n\n<p>maalai mayakkaththula maaman veettula<br />\nvela naan paaththu poven roomula-aa<br />\npaala naan kutuththaa maaman vaangale<br />\npaarththa paarvaiyila edhuvum thonala<br />\naakaa ennota meniyaiththaan ponnaaka<br />\naravanaippaan sonnaa sooraavali vekandhaan<br />\nkanmooti irukka vaippaan ullura sirikka vaippaan<br />\nkannan sarasaththula raajandhaan sokkudhu<br />\nninnu ponnonnu potudhu oru kolandhaan<br />\nnaan enni ennip paarththen<br />\nini mela solla maatten &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (then)</p>\n\n<p>kaala neraththula vaikai aaththula<br />\nnaan thaan neeraata yaarum paakkala<br />\nmanjak kezhangetuththu araichchen kallula<br />\netuththu naan poosa ennaal mutiyala<br />\naalaravam kettadhammaa alli mukam verththadhammaa<br />\nvandha aalu adhu yaarammaa sandhiranaip pola vandha<br />\nindhiranaippola angevandhaanmaamanavan thaanammaa<br />\nthottuetuththu angange muththiraivachchaan paarammaa<br />\nata rentu perum sirichchom<br />\nappuram onnaach chendhu kulichchom &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(then)</p>\n</body>\n</html>\n","4:28","","","","3","","","0","31425","","1");
