INSERT INTO songs_english VALUES("648","0","Kattu Kuyilu","K.J.Yesudas, S.P.Balasubramaniyam","","","<html>\n<head>\n	<title></title>\n</head>\n<body>\n<p>M:&nbsp;&nbsp; &nbsp;Kaattuk kuyilu manasukkulle<br />\n&nbsp;&nbsp; &nbsp;paattukkendrum panjamillai paataththaan<br />\n&nbsp;&nbsp; &nbsp;thavilai thattu thullikkittu<br />\n&nbsp;&nbsp; &nbsp;kavalai vittu kachchaikkattu aataththaan &nbsp; &nbsp;(kaattu)</p>\n\n<p>Cho:&nbsp;&nbsp; &nbsp;ellorum moththaththile sandhosam sorkkaththile<br />\n&nbsp;&nbsp; &nbsp;thallaatum neraththile ullaasam nenjaththile(kaattu)</p>\n\n<p>M:&nbsp;&nbsp; &nbsp;potaa ellaam vittuth thallu<br />\n&nbsp;&nbsp; &nbsp;pazhasai ellaam suttuth thallu<br />\n&nbsp;&nbsp; &nbsp;pudhusaa ippo porandho munnu<br />\n&nbsp;&nbsp; &nbsp;ennik kollataa toi<br />\nM:&nbsp;&nbsp; &nbsp;payanam enge ponaal enna<br />\n&nbsp;&nbsp; &nbsp;paadhai nooru aanaal enna<br />\nM:&nbsp;&nbsp; &nbsp;thottam vachchavan thanneer<br />\n&nbsp;&nbsp; &nbsp;vituvaan summaa nillataa toi<br />\nM:&nbsp;&nbsp; &nbsp;oodhakaaththu veesa utampukkulla koosa<br />\n&nbsp;&nbsp; &nbsp;kuppakkoozham paththa vachchu kaayalaam hoi<br />\nM:&nbsp;&nbsp; &nbsp;thai porakkum naalai vitiyum nalla velai<br />\n&nbsp;&nbsp; &nbsp;ponga paalu vellam pola paayalaam<br />\nDuet:&nbsp;&nbsp; &nbsp;achchu vellam pachcharisi vetti vachcha sengarumpu<br />\n&nbsp;&nbsp; &nbsp;aththanaiyum thiththikkira naal thaan hoi &nbsp; &nbsp; (kaattu)</p>\n\n<p>M:&nbsp;&nbsp; &nbsp;pandham enna sondham enna<br />\n&nbsp;&nbsp; &nbsp;ponaa enna vandhaa enna<br />\n&nbsp;&nbsp; &nbsp;uravukkellaam kavalaippatta jenmam naanilla<br />\n&nbsp;&nbsp; &nbsp;paasam vaikka nesam vaikka<br />\n&nbsp;&nbsp; &nbsp;thozhan untu vaazhavaikka<br />\n&nbsp;&nbsp; &nbsp;avanaiththavira uravukkaaran yaarum ingille<br />\n&nbsp;&nbsp; &nbsp;ulla mattum naane usira kootaththaane<br />\n&nbsp;&nbsp; &nbsp;en nanpan kettaa vaangikannu solluven<br />\nM:&nbsp;&nbsp; &nbsp;en nanpan po