INSERT INTO songs_english VALUES("648","3","Maargazhi","","","","<html>\n<head>\n	<title></title>\n</head>\n<body>\n<p>Maarkazhidhaan oti pochchu pokiyaachchu ho hoi<br />\nnaalaikkudhaan thai porakkum thedhiyaachchu ho hoi<br />\npokiyidhu pokiyidhu nandhalaalaa ho hoi<br />\nponga vaippom naalaikkuththaan nandhalaalaa ho hoi<br />\nveettula neththu varai koottina kuppaikala pottu<br />\nmoottaiyaa katti vaichchu moolaiyil theeya vachchu moottu<br />\npokattum theemaiyellaam serattum nanmaiyellaam saami<br />\npongalo pongal endru suvaarasam kaattudhu bhoomi<br />\nthaananaa thaanana thaananaa thaananaana ho hoi &nbsp; (maarkazhi)</p>\n\n<p>&nbsp;</p>\n</body>\n</html>\n","2:41","","","","2","","","0","31440","","1");
INSERT INTO songs_english VALUES("648","4","Puttam Puthu","K.J.Yesudas","S. Janaki","","<html>\n<head>\n	<title></title>\n</head>\n<body>\n<p>M:&nbsp;&nbsp; &nbsp;Aaaa aaaa puththam pudhu poo pooththadho<br />\n&nbsp;&nbsp; &nbsp;ennangalil then vaarththadho<br />\n&nbsp;&nbsp; &nbsp;mottavizha naalaanadho<br />\n&nbsp;&nbsp; &nbsp;sollati en sellak kiliye &nbsp; &nbsp; &nbsp; (puththam)</p>\n\n<p>&nbsp;&nbsp; &nbsp;vaai pesum vaarththai ellaam<br />\n&nbsp;&nbsp; &nbsp;kan pesum allavo<br />\n&nbsp;&nbsp; &nbsp;kan pesum vaarththaiyaidhaan<br />\n&nbsp;&nbsp; &nbsp;kanneerum sonnadho &nbsp; &nbsp; &nbsp; (puththam)</p>\n\n<p>M:&nbsp;&nbsp; &nbsp;paal nilaa theykindradhendru<br />\n&nbsp;&nbsp; &nbsp;pakaliravum en nenjam vazhi vitumo endrendrum<br />\nF:&nbsp;&nbsp; &nbsp;aadhavan nee thandhadhandro nilavu makal en vannam<br />\n&nbsp;&nbsp; &nbsp;ninaivukalil un ennam<br />\nM:&nbsp;&nbsp; &nbsp;karunai kontu nee thaan kaayam thannai aatra<br />\nF:&nbsp;&nbsp; &nbsp;paarvai kontu nee thaan paasa theepam etra<br />\nM:&nbsp;&nbsp; &nbsp;uyirena naan kalandhen &nbsp; &nbsp; (puththam)</p>\n\n<p>F:&nbsp;&nbsp; &nbsp;vaazhvenum kolangal indru varaindhadhu<br />\n&nbsp;&nbsp; &nbsp;un ponnullam nekizhndhadhu en pennullam<br />\nM:&nbsp;&nbsp; &nbsp;keerththisai poopaalam endru ezhundhadhu paar nam kaanam<br />\n&nbsp;&nbsp; &nbsp;vitindhadhu nam sevvaanam<br />\nF:&nbsp;&nbsp; &nbsp;koondhal meedhu poovaai naanum unnai soota<br />\nM:&nbsp;&nbsp; &nbsp;thokai unnai naan tholil indru vaanga<br />\nF:&nbsp;&nbsp; &nbsp;unakkena naan pirandhen &nbsp; &nbsp;(puththamm)</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n</body>\n</html>\n","5:02","","","","5","","","0","31441","","1");
INSERT INTO songs_english VALUES("648","5","Raakamma Kaiyatha","S.P.Balasubramaniyam","Swarnalatha","","<html>\n<head>\n	<title></title>\n</head>\n<body>\n<p>M:&nbsp;&nbsp; &nbsp;Ati raakkammaa kaiyadhattu pudhu raakaththil mettukkattu<br />\n&nbsp;&nbsp; &nbsp;ati raakkozhi melam kottu<br />\n&nbsp;&nbsp; &nbsp;indha raasaavin nenja thottu<br />\nF:&nbsp;&nbsp; &nbsp;ata raasaave pandhal nattu pudhu rosaappoo maalai kattu<br />\n&nbsp;&nbsp; &nbsp;adhai raasaaththi tholil ittu<br />\n&nbsp;&nbsp; &nbsp;thinam raavellaam thaalam kottu<br />\nM:&nbsp;&nbsp; &nbsp;idhu kattuk kaaval idhu oththukkaadhu<br />\n&nbsp;&nbsp; &nbsp;idhai katti pota oru sooran edhu<br />\n&nbsp;&nbsp; &nbsp;jaangu jakku jajakku jakku<br />\n&nbsp;&nbsp; &nbsp;jaangu jakku jaam.. &nbsp; &nbsp; &nbsp; &nbsp;(ati)</p>\n\n<p>M:&nbsp;&nbsp; &nbsp;ther izhukkum naalum theppam vitum naalum<br />\n&nbsp;&nbsp; &nbsp;machchaan inge adhu en kooru<br />\nF:&nbsp;&nbsp; &nbsp;ata ooru sanam yaavum otrumaiyaa serum<br />\n&nbsp;&nbsp; &nbsp;vampum thumpum illa nee paaru<br />\nM:&nbsp;&nbsp; &nbsp;maththala saththam ettu ooru thaan<br />\n&nbsp;&nbsp; &nbsp;ettanum thampi ati joraaka<br />\n&nbsp;&nbsp; &nbsp;vaikkira paanam andha vaanaiye<br />\n&nbsp;&nbsp; &nbsp;thaikkanum thampi vitu neraaka<br />\n&nbsp;&nbsp; &nbsp;ata thampattam thaaraidhaan thattippaatu<br />\nM:&nbsp;&nbsp; &nbsp;muththammaa muththam sindhu<br />\n&nbsp;&nbsp; &nbsp;pani muththu pol niththam vandhu<br />\nF:&nbsp;&nbsp; &nbsp;ata maamaa nee jallikkattu nee meyaadhe thullikkittu<br />\nM:&nbsp;&nbsp; &nbsp;ati pakkam needhaan oru vaikka poru<br />\n&nbsp;&nbsp; &nbsp;unnai konjam menjaa thinam akkapporu &nbsp; &nbsp; (ati)<br />\nM:&nbsp;&nbsp; &nbsp;vaasalukku vaasal vanna vanna maadhu<br />\n&nbsp;&nbsp; &nbsp;inge ange oli vilakkeththu<br />\nF:&nbsp;&nbsp; &nbsp;ata kuththiruttu pochchu patta pakal aachchu<br />\n&nbsp;&nbsp; &nbsp;engum inpam itum pooppooththu<br />\nM:&nbsp;&nbsp; &nbsp;na