INSERT INTO songs_english VALUES("648","6","Sundari","S.P.Balasubramaniyam","S. Janaki","","<html>\n<head>\n	<title></title>\n</head>\n<body>\n<p>M:&nbsp;&nbsp; &nbsp;Sundhari kannaal oru sedhi<br />\n&nbsp;&nbsp; &nbsp;sollati innaal nalla thedhi<br />\nF:&nbsp;&nbsp; &nbsp;ennaiye thandhen unakkaaka<br />\n&nbsp;&nbsp; &nbsp;jenmame konten adharkaaka<br />\nM:&nbsp;&nbsp; &nbsp;naanunai neenga maatten<br />\n&nbsp;&nbsp; &nbsp;neenginaal thoonga maatten<br />\n&nbsp;&nbsp; &nbsp;serndhadhe nam jeevano &nbsp; &nbsp; &nbsp; (sundhari)</p>\n\n<p>F:&nbsp;&nbsp; &nbsp;vaai mozhindha vaarththai yaavum<br />\n&nbsp;&nbsp; &nbsp;kaatril ponaal niyaayamaa<br />\nM:&nbsp;&nbsp; &nbsp;paai viriththu paavai paarththu kaadhal inpam maayama..<br />\nM:&nbsp;&nbsp; &nbsp;aa.aa.vaal pitiththu nindraal koota<br />\n&nbsp;&nbsp; &nbsp;nenjil undhan oorvalam<br />\n&nbsp;&nbsp; &nbsp;porkalaththil saaindhaal koota<br />\n&nbsp;&nbsp; &nbsp;jeevan unnai serndhitum<br />\nF:&nbsp;&nbsp; &nbsp;thenilavu naanvaata enindha sodhanai<br />\nM:&nbsp;&nbsp; &nbsp;vaan nilavai nee kelu koorum en vedhanai<br />\nF:&nbsp;&nbsp; &nbsp;enaidhaan anpe marandhaayo<br />\nM:&nbsp;&nbsp; &nbsp;marappen endre ninaiththaayo &nbsp; &nbsp;(ennaiye)</p>\n\n<p>F:&nbsp;&nbsp; &nbsp;solaiyilum mutkal thondrum<br />\n&nbsp;&nbsp; &nbsp;naanum neeyum neenginaal<br />\n&nbsp;&nbsp; &nbsp;paaraiyilum pookkal pookkum<br />\n&nbsp;&nbsp; &nbsp;naanun maarpil thoonginaal<br />\nM:&nbsp;&nbsp; &nbsp;maadhangalum vaaramaakum naanum neeyum kootinaal<br />\n&nbsp;&nbsp; &nbsp;vaarangalum maadhamaakum paadhai maari otinaal<br />\nF:&nbsp;&nbsp; &nbsp;koti sukam vaaraadho nee ennai thetinaal<br />\nM:&nbsp;&nbsp; &nbsp;kaayangalum aaraadho nee edhir thondrinaal<br />\nF:&nbsp;&nbsp; &nbsp;utane vandhaal uyir vaazhum<br />\nM:&nbsp;&nbsp; &nbsp;varuven annaal varakkootum &nbsp; &nbsp; &nbsp; (sundhari)</p>\n</body>\n</html>\n","7:14","","","","2","","","0","31443","","1");
