		foreach(glob("../plugins/".$plug."/*.html") as $ed_plug_file):
		$fp=fopen($ed_plug_file,'w');
		$yes = fwrite_stream($fp,$_REQUEST['content']);
		echo ($yes)?"Content updated successfully !":"Error while update this plugin !";
		header("refresh:1;");
		endforeach;
	}
	
	
	
