	// Admin login check
	public function dev_login($get){
		$msg="";
		if(!isset($_SESSION['DEVFRAME_LOGIN'])):
		if($_REQUEST['login']!='' && $_REQUEST['password']!=''){
		$que = mysql_query("select * from ".DB_PREFIX."admin where username='".$_REQUEST['login']."' and password='".$_REQUEST['password']."'");
		$fetch = mysql_fetch_object($que);
		$count = mysql_num_rows($que);
		if($count > 0){ 
		/* session in database */
		    $en_pass = encode('pass');
			$expiry  = time()+1800;
		    $status = array('admin'=>$en_pass,'overlay'=>'true','time'=>$expiry);
			if(num(DB_PREFIX."session_data")==0){
			foreach($status as $session=>$data){
			query("insert into ".DB_PREFIX."session_data set session='".$session."',data='".$data."',status='1'");
			}
			}else{
			foreach($status as $session=>$data){
			query("update ".DB_PREFIX."session_data set data='".$data."' where session='".$session."'");
			}
			}
		/* session in database */
		if(isset($_SESSION['devframe_session_url'])){ 
		location($_SESSION['devframe_session_url']);
		}else{
		location("../?overlay=Home");
		}
		 }
		else{ location("login-error.php"); exit; }
		}else{
		$msg = "<span class='error'>Please enter username and password !</span>";
		}
	    else:
		location("../?overlay=Home");
		endif;	
		return $msg;	
	}
